var React = require('react'),
    div = React.DOM.div,
    a = React.DOM.a;

module.exports = React.createClass({
  
  render: function(){

    var content = { __html: this.props.content };

    return div({ id: 'article' },

      a({ className: 'source', href: this.props.source_url }, this.props.source_domain ),
      div({ className: 'title' }, this.props.title ),
      div({ className: 'content', dangerouslySetInnerHTML: content })
    );
  }
});