var React = require('react'),
    span = React.DOM.span,
    div = React.DOM.div,
    a = React.DOM.a;

module.exports = React.createClass({
  
  render: function(){

    var content = { __html: this.props.content };

    return div({ id: 'article' },

      a({ className: 'bookmarks-link', href: '/bookmarks' }, 
        span({ className: 'icon icon-couch'}),
        'bookmarks' 
      ),

      a({ className: 'source', href: this.props.source_url }, this.props.source_domain ),
      div({ className: 'title' }, this.props.title ),      

      React.createElement( require('app/client/src/js/article-actions-template'), { bookmark: this.props.bookmark }),
      
      div({ className: 'content', dangerouslySetInnerHTML: content }),

      React.createElement( require('app/client/src/js/article-actions-template'), { bookmark: this.props.bookmark, footer: true }),

      a({ className: 'bookmarks-link footer-link', href: '/bookmarks' }, 
        span({ className: 'icon icon-couch'}),
        'bookmarks' 
      )
    );
  }
});