var Noticeboard = require('cjs-noticeboard'),
    board = new Noticeboard();

require('jquery')( document ).ready( function(){

  require('app/client/src/js/events/state')( board );
  require('app/client/src/js/events/store')( board );
  require('app/client/src/js/events/view')( board );
  require('app/client/src/js/events/browser')( board );
  require('app/client/src/js/events/api')( board );

  board.notify('bootstrap-completed', null, 'app-behavior' );
});