var api_token,
    jQuery = require('jquery'),
    store = require('store/store+json2.min.js');

jQuery( document ).ready( function(){
  
  if( !store.enabled ) return console.log( 'can\'t work offline -- local storage is required to do so but is unavailable' );

  api_token = store.get( 'api-token' );

  if( !api_token ){    

    jQuery.get('/api-token', function( token ){

      if( token ){

        api_token = token;
        store.set( 'api-token', token );
        console.log( 'got api token: ' + token );
      } 
    });
  }

  else {

    console.log( api_token ); 
  }
});