var Noticeboard = require('cjs-noticeboard'),
    noticeboard = new Noticeboard();

require('jquery')( document ).ready( function(){

  require('app/client/src/js/events/state')( noticeboard );
  require('app/client/src/js/events/store')( noticeboard );
  require('app/client/src/js/events/view')( noticeboard );
  require('app/client/src/js/events/browser')( noticeboard );
  require('app/client/src/js/events/api')( noticeboard );

  noticeboard.notify('bootstrap-completed', null, 'app-behavior' );
});