var React = require('react'),
    div = React.DOM.div;

module.exports = React.createClass({

  render: function(){

    var error = null;

    if( this.props.credentials_error ){

      error = React.createElement( require('app/client/src/js/login-error-modal'), {

        type: 'client',
        message: "invalid username or password"
      });
    } 
    
    else if( this.props.server_error ){

      error = React.createElement( require('app/client/src/js/login-error-modal'), {

        type: 'server',
        message: "it's not you, it's us - please try again later"
      });
    }

    return div({ id: 'logged-out' },

      require('app/client/src/js/home-landing-template'),
      error,      
      require('app/client/src/js/login-form-template')
    );
  }
});
