var React = require('react'),
    span = React.DOM.span,
    div = React.DOM.div,
    a = React.DOM.a;

module.exports = React.createClass({

  render: function() {

    var page = this.props.page || 'bookmarks',
        page_icon,

        bookmarks_tab,
        archives_tab,
        faves_tab;

    switch( page ){

      case 'bookmarks':

        page_icon = 'icon-couch';
      break;

      case 'favorites':

        page_icon = 'icon-favorite';
      break;

      case 'archives':

        page_icon = 'icon-archive';
      break;
    }

    bookmarks_tab = div({ className: 'tab' + ( page === 'bookmarks' ? ' active' : '' ) },

      ( page !== 'bookmarks' ? a({ className: 'icon icon-couch', href: '/bookmarks', title: 'all bookmarks' }) : span({ className: 'icon icon-couch' }) )
    );

    archives_tab = div({ className: 'tab' + ( page === 'archives' ? ' active' : '' ) },

      ( page !== 'archives' ? a({ className: 'icon icon-archive', href: '/archives', title: 'archived bookmarks' }) : span({ className: 'icon icon-archive' }) )
    );

    faves_tab = div({ className: 'tab' + ( page === 'favorites' ? ' active' : '' ) },

      ( page !== 'favorites' ? a({ className: 'icon icon-favorite', href: '/favorites', title: 'favorite bookmarks' }) : span({ className: 'icon icon-favorite' }) )
    );

    return div({ className: 'header' },

      div({ className: 'page-title' },

        span({ className: 'icon ' + page_icon }),
        page
      ),

      div({ className: 'filter-tabs' },

        bookmarks_tab,
        faves_tab,
        archives_tab
      )
    );
  }
});