var React = require('react'),
    div = React.DOM.div,
    a = React.DOM.a,

    htmlEntities = require('html-entities').AllHtmlEntities;

module.exports = React.createClass({

  render: function(){

    var bookmarks = [];

    this.props.bookmarks.forEach( function( bookmark, index ){

      bookmarks.push( 
        div({ className: 'bookmark', key: bookmark.id }, 
          a({ className: 'source', href: bookmark.article.url }, bookmark.article.domain ),
          div({ className: 'title' }, htmlEntities.decode( bookmark.article.title ) ),
          div({ className: 'excerpt' }, htmlEntities.decode( bookmark.article.excerpt ) )
        ));
    });

    return div({ id: 'bookmarks-list' }, bookmarks );
  }
});
