var React = require('react'),
    div = React.DOM.div,
    a = React.DOM.a;

module.exports = React.createClass({
  
  render: function(){

    var has_error = this.props.error;

    if( has_error ){

      return div({ id: 'bookmarks', className: 'error' }, 

        div({ className: 'error-msg' },

          div({ className: 'title' }, 'Having troubles getting your bookmarks at the moment'),
          div({ className: 'subtitle' }, 'Please try again later')
        )
      );
    }

    else {

      return div({ id: 'bookmarks' },

        React.createElement( require('app/client/src/js/index-header-template'), { page: this.props.page } ),
        React.createElement( require('app/client/src/js/index-pagination-template'), { metadata: this.props.metadata, page: this.props.page } ),
        React.createElement( require('app/client/src/js/index-list-template'), { bookmarks: this.props.bookmarks } ),
        React.createElement( require('app/client/src/js/index-pagination-template'), { metadata: this.props.metadata, page: this.props.page } )
      );
    }
  }
});