var React = require('react'),
    span = React.DOM.span,
    div = React.DOM.div,
    a = React.DOM.a,

    htmlEntities = require('html-entities').AllHtmlEntities;

module.exports = React.createClass({

  render: function(){

    var page_type = this.props.page || 'bookmarks',
        current_page = this.props.metadata.page,
        has_prev_page = current_page > 1 && this.props.metadata.num_pages > 1,
        has_next_page = current_page < this.props.metadata.num_pages,

        prev_page = ! has_prev_page ? span({ className: 'prev-page inactive' }, htmlEntities.decode( '&laquo; prev' ) ) : a({ className: 'prev-page', href: '/' + page_type + '/' + ( current_page - 1 ) }, htmlEntities.decode( '&laquo; prev' ) ),
        next_page = ! has_next_page ? span({ className: 'next-page inactive' }, htmlEntities.decode( 'next &raquo;' ) ) : a({ className: 'next-page', href: '/' + page_type + '/' + ( current_page + 1 ) }, htmlEntities.decode( 'next &raquo;' ) );

    return div({ className: 'pagination'},

      prev_page,
      div({ className: 'current' }, current_page ),
      next_page
    );
  }
});