var React = require('react'),
    div = React.DOM.div,
    a = React.DOM.a,

    htmlEntities = require('html-entities').AllHtmlEntities;

module.exports = React.createClass({

  render: function(){

    var current_page = this.props.metadata.page,
        has_prev_page = current_page > 1 && this.props.metadata.num_pages > 1,
        has_next_page = current_page < this.props.metadata.num_pages,

        prev_page = ! has_prev_page ? null : a({ className: 'prev-page', href: '/bookmarks/' + ( current_page - 1 ) }, htmlEntities.decode( '&laquo; prev' ) ),
        next_page = ! has_next_page ? null : a({ className: 'next-page', href: '/bookmarks/' + ( current_page + 1 ) }, htmlEntities.decode( 'next &raquo;' ) );

    return div({ className: 'pagination'},

      prev_page,
      div({ className: 'current' }, current_page ),
      next_page
    );
  }
});