var React = require('react'),
    div = React.DOM.div;

module.exports = React.createClass({
  
  render: function(){

    var error = null,
        type = this.props.type,
        message = this.props.message;

    if( type && message ){

      error = div({ className: 'login-error ' + type }, message );
    }

    return error;
  }
})