var React = require('react'),
    div = React.DOM.div,
    form = React.DOM.form,
    input = React.DOM.input;

module.exports = React.createClass({

  render: function(){    

    var error = null;

    if( this.props.credentials_error ){

      error = React.createElement( require('app/client/src/js/login-form-error-modal'), {

        type: 'client',
        message: "invalid username or password"
      });
    } 
    
    else if( this.props.server_error ){

      error = React.createElement( require('app/client/src/js/login-form-error-modal'), {

        type: 'server',
        message: "it's not you, it's us - please try again later"
      });
    } 

    return form({ className: 'login-form', action: 'login', method: 'post' },
      
      error,
      input({ type: 'text', name: 'user', placeholder: 'username' }),
      input({ type: 'password', name: 'pass', placeholder: 'password' }),
      input({ type: 'submit', value: 'LOGIN' })
    );
  }
})